import React, { useState, useEffect } from 'react';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Button, Box, List, ListItem, ListItemText, Divider } from '@mui/material';
import api from '../services/api';
import Navbar from '../components/Navbar';
import EditMovie from '../components/EditMovie';

function Movies() {
  const [movies, setMovies] = useState([]);
  const [editingMovie, setEditingMovie] = useState(null);
  const [expandedMovie, setExpandedMovie] = useState(null);
  const [movieSubscriptions, setMovieSubscriptions] = useState({});

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await api.get('/movies');
      setMovies(response.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/movies/${id}`);
      setMovies(movies.filter(movie => movie._id !== id));
    } catch (error) {
      console.error('Error deleting movie:', error);
    }
  };

  const handleEdit = (movie) => {
    setEditingMovie(movie);
  };

  const handleCloseEdit = () => {
    setEditingMovie(null);
  };

  const updateMovie = (updatedMovie) => {
    setMovies(movies.map(movie => movie._id === updatedMovie._id ? updatedMovie : movie));
  };

  const toggleSubscriptions = async (id) => {
    if (expandedMovie === id) {
      setExpandedMovie(null);
    } else {
      setExpandedMovie(id);
      if (!movieSubscriptions[id]) {
        try {
          const response = await api.get(`/subscriptions/movies/${id}`);
          setMovieSubscriptions({...movieSubscriptions, [id]: response.data});
        } catch (error) {
          console.error('Error fetching subscriptions:', error);
        }
      }
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <>
      <Navbar />
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Movies
        </Typography>
        <Grid container spacing={4}>
          {movies.map((movie) => (
            <Grid item key={movie._id} xs={12} sm={6} md={4}>
              <Card>
                <CardMedia
                  component="img"
                  height="140"
                  image={movie.imageUrl}
                  alt={movie.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = 'https://via.placeholder.com/140x200?text=No+Image';
                  }}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {movie.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Year: {movie.yearPremiered}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Genres: {movie.genres.join(', ')}
                  </Typography>
                  <Box sx={{ mt: 2 }}>
                    <Button size="small" color="primary" onClick={() => handleEdit(movie)}>
                      Edit
                    </Button>
                    <Button size="small" color="secondary" onClick={() => handleDelete(movie._id)}>
                      Delete
                    </Button>
                    <Button size="small" onClick={() => toggleSubscriptions(movie._id)}>
                      {expandedMovie === movie._id ? 'Hide Subscriptions' : 'Show Subscriptions'}
                    </Button>
                  </Box>
                  {expandedMovie === movie._id && (
                    <Box sx={{ mt: 2 }}>
                      <Typography variant="subtitle1">Subscriptions:</Typography>
                      {movieSubscriptions[movie._id] ? (
                        movieSubscriptions[movie._id].length > 0 ? (
                          <List dense>
                            {movieSubscriptions[movie._id].map((sub, index) => (
                              <React.Fragment key={sub._id}>
                                <ListItem>
                                  <ListItemText
                                    primary={sub.memberName}
                                    secondary={`Watched on: ${formatDate(sub.date)}`}
                                  />
                                </ListItem>
                                {index < movieSubscriptions[movie._id].length - 1 && <Divider />}
                              </React.Fragment>
                            ))}
                          </List>
                        ) : (
                          <Typography variant="body2">No subscriptions for this movie.</Typography>
                        )
                      ) : (
                        <Typography variant="body2">Loading subscriptions...</Typography>
                      )}
                    </Box>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      {editingMovie && (
        <EditMovie
          movie={editingMovie}
          open={Boolean(editingMovie)}
          handleClose={handleCloseEdit}
          updateMovie={updateMovie}
        />
      )}
    </>
  );
}

export default Movies;
