import React, { useState, useEffect } from 'react';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Button, Box } from '@mui/material';
import api from '../services/api';


function Movies() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await api.get('/movies');
      setMovies(response.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/movies/${id}`);
      setMovies(movies.filter(movie => movie._id !== id)); // Update state locally
    } catch (error) {
      console.error('Error deleting movie:', error);
    }
  };

  const [expandedMovie, setExpandedMovie] = useState(null);

  const toggleSubscriptions = (id) => {
    setExpandedMovie(expandedMovie === id ? null : id);
  };

  return (
    <>
     
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Movies
        </Typography>
        <Grid container spacing={4}>
          {movies.map((movie) => (
            <Grid item key={movie._id} xs={12} sm={6} md={4}>
              <Card>
                <CardMedia
                  component="img"
                  height="140"
                  image={movie.imageUrl}
                  alt={movie.name}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {movie.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Year: {movie.yearPremiered}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Genres: {movie.genres.join(', ')}
                  </Typography>
                  <Box sx={{ mt: 2 }}>
                    <Button size="small" color="primary">
                      Edit
                    </Button>
                    <Button size="small" color="secondary" onClick={() => handleDelete(movie._id)}>
                      Delete
                    </Button>
                    <Button size="small" onClick={() => toggleSubscriptions(movie._id)}>
                      {expandedMovie === movie._id ? 'Hide Subscriptions' : 'Show Subscriptions'}
                    </Button>
                  </Box>
                  {expandedMovie === movie._id && (
                    <Box sx={{ mt: 2 }}>
                      <Typography variant="body2">
                        Subscriptions will be displayed here
                      </Typography>
                    </Box>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </>
  );
}

export default Movies;
