import React, { useState, useEffect } from 'react';
import { Box, Button, FormControl, InputLabel, Select, MenuItem, TextField, Typography } from '@mui/material';

function AddSubscription({ movies, onSubscribe, memberId }) {
  const [selectedMovie, setSelectedMovie] = useState('');
  const [watchDate, setWatchDate] = useState('');

  useEffect(() => {
    // Reset selected movie when movies list changes
    setSelectedMovie('');
  }, [movies]);

  const handleSubscribe = () => {
    if (selectedMovie && watchDate) {
      const formattedDate = new Date(watchDate).toISOString();
      onSubscribe(memberId, selectedMovie, formattedDate);
      setSelectedMovie('');
      setWatchDate('');
    }
  };

  if (movies.length === 0) {
    return (
      <Box sx={{ mt: 2 }}>
        <Typography>No unwatched movies available for subscription.</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ mt: 2 }}>
      <FormControl fullWidth sx={{ mb: 2 }}>
        <InputLabel id="movie-select-label">Select Movie</InputLabel>
        <Select
          labelId="movie-select-label"
          value={selectedMovie}
          label="Select Movie"
          onChange={(e) => setSelectedMovie(e.target.value)}
        >
          {movies.map((movie) => (
            <MenuItem key={movie._id} value={movie._id}>
              {movie.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <TextField
        label="Watch Date"
        type="date"
        value={watchDate}
        onChange={(e) => setWatchDate(e.target.value)}
        fullWidth
        InputLabelProps={{
          shrink: true,
        }}
        sx={{ mb: 2 }}
      />
      <Button 
        variant="contained" 
        color="primary" 
        onClick={handleSubscribe} 
        fullWidth
        disabled={!selectedMovie || !watchDate}
      >
        Subscribe
      </Button>
    </Box>
  );
}

export default AddSubscription;
