import React, { useState } from 'react';
import { Box, Button, FormControl, InputLabel, Select, MenuItem, TextField } from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { format } from 'date-fns'; // Import format function

function AddSubscription({ movies, onSubscribe, memberId }) {
  const [selectedMovie, setSelectedMovie] = useState('');
  const [watchDate, setWatchDate] = useState(null);

  const handleSubscribe = () => {
    if (selectedMovie && watchDate) {
      // Format the date as an ISO string
      const formattedDate = format(watchDate, "yyyy-MM-dd'T'HH:mm:ss.SSSxxx");
      onSubscribe(memberId, selectedMovie, formattedDate);
      setSelectedMovie('');
      setWatchDate(null);
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <FormControl fullWidth sx={{ mb: 2 }}>
        <InputLabel id="movie-select-label">Select Movie</InputLabel>
        <Select
          labelId="movie-select-label"
          value={selectedMovie}
          label="Select Movie"
          onChange={(e) => setSelectedMovie(e.target.value)}
        >
          {movies.map((movie) => (
            <MenuItem key={movie._id} value={movie._id}>
              {movie.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <LocalizationProvider dateAdapter={AdapterDateFns}>
        <DatePicker
          label="Watch Date"
          value={watchDate}
          onChange={(newValue) => setWatchDate(newValue)}
          renderInput={(params) => <TextField {...params} fullWidth sx={{ mb: 2 }} />}
        />
      </LocalizationProvider>
      <Button variant="contained" color="primary" onClick={handleSubscribe} fullWidth>
        Subscribe
      </Button>
    </Box>
  );
}

export default AddSubscription;
